create table accounts (
	id BIGSERIAL PRIMARY KEY NOT NULL,
	first_name VARCHAR(50) NOT NULL,
	last_name VARCHAR(50) NOT NULL,
	email VARCHAR(50),
	balance INT NOT NULL
);

BEGIN;

UPDATE accounts SET balance = balance - 7000 WHERE id = 100;

UPDATE accounts SET balance = balance + 7000 WHERE id = 99;

COMMIT;

ROLLBACK;
